import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    token:undefined || window.localStorage.getItem('token')
  },
  getters: {

  },
  mutations: {
    userLogin(state,token){
      state.token = token
      window.localStorage.setItem("token", token)
    }
  },
  actions: {
  },
  modules: {
  }
})
