import Vue from 'vue'
import VueRouter from 'vue-router'
import UserLogin from "@/views/UserLogin";
import UserReg from "@/views/UserReg";
import Home from "@/views/Home";
import HomeBody from "@/views/HomeBody";


Vue.use(VueRouter)

const routes = [
    // {
    //     path: '/',
    //     component: Home
    // },
    // {
    //     path: '/userLogin',
    //     component: UserLogin
    // },
    // {
    //     path: '/userReg',
    //     component: UserReg
    // },
    {
        path: "/home",
        redirect:"/homeBody"
    },
    {
        path: '/home',
        component: Home,
        children: [
            {
                path: '/userLogin',
                component: UserLogin,
            },
            {
                path: '/userReg',
                component: UserReg,
            },
            {
                path: '/homeBody',
                component: HomeBody,
            }

        ]
    }
]

const router = new VueRouter({
    routes,
    mode: 'history'
})

export default router
