export default {
  name: "UserReg",
  data() {
    return {
      regUser: {
        phone: '',
        password: ''
      }
    };
  },
  methods: {
    reg() {
      this.$axios.post("/user/register", this.regUser).then(resp => {
        if (resp.data.code === 200) {
          this.$message.success("注册成功！");
        } else {
          this.$message.error(resp.data.data);
        }
      });
    }
  }
};
